mapper.select:
select count(*) as count
from ${tname}

	where t_recognize = 0

 		SELECT * ,
		if(l_recognize = 0 ,"승인대기","승인거부") AS request,
		(SELECT c_name FROM category WHERE category.c_code = lecture_apply.c_code) AS c_name
		FROM lecture_apply
		where l_recognize != 1

  		AND la_no = ${la_no}

SELECT *
FROM notice
WHERE n_no = #{n_no}

mapper.update:
 		UPDATE notice
		SET n_title = #{n_title}, n_content = #{n_content}
		WHERE u_id = #{u_id}
