mapper.select:
		SELECT q.*, v.c_name,v.lqar_title,v.lqar_content, if(q.lqa_confirm=0,"답변미완료","답변완료")AS confirm 
		FROM lecture_qna_view v RIGHT JOIN lecture_qna q
		ON v.lqa_no = q.lqa_no


  		where c_name = #{c_name}


  		ORDER BY lqa_no

  		limit #{page.startPage},#{page.lastPage}

select * 
from user_board
order by b_no desc;
